// src/components/SimulatorInputForm.js
import React, { useState } from 'react';

const SimulatorInputForm = ({ inputs, onChange }) => {
  const [errors, setErrors] = useState({});

  const handleInputChange = (index, value) => {
    const newErrors = { ...errors };
    if (value === '' || isNaN(value)) {
      newErrors[index] = 'Please enter a valid number';
    } else {
      delete newErrors[index];
    }
    setErrors(newErrors);
    onChange(index, value);
  };

  const isFormValid = () => {
    return inputs.every((input) => input.value !== '');
  };

  return (
    <div className="sim-input-form">
      {inputs.map((input, index) => (
        <div key={index} className="input-row">
          <label htmlFor={`input-${index}`}>{input.label}</label>
          <input
            type="text"
            id={`input-${index}`}
            value={input.value}
            onChange={(e) => handleInputChange(index, e.target.value)}
            className={errors[index] ? 'error' : ''}
          />
          {errors[index] && <span className="error-message">{errors[index]}</span>}
        </div>
      ))}
      {!isFormValid() && <p className="error-message">Please fill in all fields</p>}
    </div>
  );
};

export default SimulatorInputForm;
