// src/components/ContentContainer.js
import React, { useState } from 'react';
import SimulatorButton from './SimulatorButton';
import SimulatorPopup from './SimulatorPopup';

const ContentContainer = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [selectedSimulator, setSelectedSimulator] = useState(null);

  const simulators = [
    { id: 1, name: 'Simulator 1' },
    { id: 2, name: 'Simulator 2' },
    { id: 3, name: 'Simulator 3' },
    { id: 4, name: 'Simulator 4' },
    { id: 5, name: 'Simulator 5' },
  ];

  const handleSimulatorButtonClick = (simulator) => {
    setSelectedSimulator(simulator);
    setShowPopup(true);
  };

  const handlePopupClose = () => {
    setShowPopup(false);
    setSelectedSimulator(null);
  };

  return (
    <div className="content-container">
      <h2>Favorite</h2>
      <hr />
      <div className="simulator-buttons">
        {simulators.map((simulator) => (
          <SimulatorButton
            key={simulator.id}
            simulator={simulator}
            onClick={() => handleSimulatorButtonClick(simulator)}
          />
        ))}
      </div>
      <hr />
      {/* 빈 공간 */}
      {showPopup && (
        <SimulatorPopup
          simulator={selectedSimulator}
          onClose={handlePopupClose}
        />
      )}
    </div>
  );
};

export default ContentContainer;
