import React from 'react';

const Sidebar = () => {
  const processes = [
    { id: 1, name: '대공정 1', subProcesses: [
      { id: 1, name: '소공정 1', subProcesses: [
        { id: 1, name: '세부공정 1' },
        { id: 2, name: '세부공정 2' },
      ]},
      { id: 2, name: '소공정 2', subProcesses: [
        { id: 3, name: '세부공정 3' },
        { id: 4, name: '세부공정 4' },
      ]},
    ]},
    { id: 2, name: '대공정 2', subProcesses: [
      { id: 3, name: '소공정 3', subProcesses: [
        { id: 5, name: '세부공정 5' },
        { id: 6, name: '세부공정 6' },
      ]},
    ]},
  ];

  return (
    <div className="sidebar">
      <h2>공정 분류</h2>
      <ul>
        {processes.map((process) => (
          <li key={process.id}>
            {process.name}
            <ul>
              {process.subProcesses.map((subProcess) => (
                <li key={subProcess.id}>
                  {subProcess.name}
                  <ul>
                    {subProcess.subProcesses.map((subSubProcess) => (
                      <li key={subSubProcess.id}>{subSubProcess.name}</li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
