// frontend/src/components/SimulatorInputForm.js
import React from "react";

const SimulatorInputForm = ({ inputs, onChange }) => {
  const handleInputChange = (index, value) => {
    // 소수점 둘째 자리까지만 허용하는 정규식
    const regex = /^\d+(\.\d{0,2})?$/;

    if (regex.test(value)) {
      onChange(index, value);
    }
  };

  return (
    <div className="simulator-input-form">
      {inputs.map((input, index) => (
        <div key={index}>
          <label>{input.label}</label>
          <input
            type="number"
            step="0.01"
            value={input.value}
            onChange={(e) => handleInputChange(index, e.target.value)}
          />
          <span className="range">[{input.min}~{input.max}]</span>
          {input.error && <span className="error">{input.error}</span>}
        </div>
      ))}
    </div>
  );
};

export default SimulatorInputForm;
