// src/components/Sidebar.js
import React from 'react';
import { Treebeard } from 'react-treebeard';
import './react-treebeard-style.css';

const data = {
  name: 'Root',
  toggled: true,
  children: [
    {
      name: '대공정 1',
      children: [],
    },
    {
      name: '대공정 2',
      toggled: true,
      children: [
        {
          name: '소공정 3',
          children: [],
        },
        {
          name: '소공정 4',
          toggled: true,
          children: [
            { name: '세부공정 5' },
            { name: '세부공정 6' },
          ],
        },
      ],
    },
  ],
};

const Sidebar = () => {
  const onToggle = (node, toggled) => {
    if (node.children) {
      node.toggled = toggled;
    }
  };

  return (
    <div className="sidebar">
      <Treebeard data={data} onToggle={onToggle} />
    </div>
  );
};

export default Sidebar;
