// src/pages/SimulatorPage.js
import React, { useState } from 'react';
import SimulatorHeader from '../components/SimulatorHeader';
import SimulatorInputForm from '../components/SimulatorInputForm';
import SimulatorContentContainer from '../components/SimulatorContentContainer';
import SimulatorFooter from '../components/SimulatorFooter';
import './SimulatorPage.css';

const SimulatorPage = () => {
  const [inputs, setInputs] = useState([
    { label: 'iparam1 [10mm~50mm]', min: 10, max: 50, value: '' },
    { label: 'iparam2 [10mm~50mm]', min: 10, max: 50, value: '' },
    { label: 'iparam3 [10mm~50mm]', min: 10, max: 50, value: '' },
    { label: 'iparam4 [10mm~50mm]', min: 10, max: 50, value: '' },
    { label: 'iparam5 [10mm~50mm]', min: 10, max: 50, value: '' },
  ]);

  const [isSimulated, setIsSimulated] = useState(false);
  const [simulationResult, setSimulationResult] = useState('');

  const handleInputChange = (index, value) => {
    const newInputs = [...inputs];
    newInputs[index].value = value;
    setInputs(newInputs);
  };

  const handleSimulate = () => {
    // 시뮬레이션 로직 구현
    // 입력 값을 기반으로 시뮬레이션 수행
    // 시뮬레이션 결과를 simulationResult 상태에 저장
    const result = '시뮬레이션 결과 리포트';
    setSimulationResult(result);
    setIsSimulated(true);
  };

  return (
    <div className="simulator-page">
      <SimulatorHeader title="Simulator" onSimulate={handleSimulate} />
      <div className="simulator-content">
        <SimulatorInputForm inputs={inputs} onChange={handleInputChange} />
        <SimulatorContentContainer
          isSimulated={isSimulated}
          description="입력 인자에 대한 설명 이미지"
          report={simulationResult}
        />
      </div>
      <SimulatorFooter />
    </div>
  );
};

export default SimulatorPage;
